import React, { useEffect, useState } from "react";
import "./Coin.css";
import { Button } from "web3uikit";
import { useWeb3ExecuteFunction, useMoralis } from 'react-moralis';

function Coin({ perc, setPerc, token, setModalToken, setVisible }) {
  const [color, setColor] = useState();
  const contractProcessor = useWeb3ExecuteFunction();
  const { isAuthenticated } = useMoralis();


  useEffect(() => {
    if (perc < 50) {
      setColor("#c43d08");
    } else {
      setColor("green");
    }
  }, [perc]);



  async function vote(upDown) {
    let options = {
      contractAddress: "0xeb0C941A46CFc77236b5C621eD2938efdC5dc3af",
      functionName: "vote",
      abi: [{"inputs":[{"internalType":"string","name":"_ticker","type":"string"},{"internalType":"bool","name":"_vote","type":"bool"}],"name":"vote","outputs":[],"stateMutability":"nonpayable","type":"function"}],
      params: {}
  }
}

return (
  <>
    <div>
      <div className="token">{token}</div>
      <div className="circle" style={{ boxShadow: `0 0 20px ${color}` }}>
        <div
          className="wave"
          style={{
            marginTop: `${100 - perc}%`,
            boxShadow: `0 0 20px ${color}`,
            backgroundColor: color,
          }}
        ></div>
        <div className="percentage">{perc}%</div>
      </div>

      <div className="votes">
        <Button
          onClick={() => {
            setPerc(perc + 1);
          }}
          text="up"
          theme="primary"
          type="button"
        />
        <Button
          color="red"
          onClick={() => {
            setPerc(perc - 1);
          }}
          text="Down"
          theme="colored"
          type="button"
        />
      </div>
      <div className="votes">
        <Button
          color="red"
          onClick={() => {
            setModalToken(token);
            setVisible(true);
          }}
          text="INFO"
          theme="translucent"
          type="button"
        />
      </div>
    </div>
  </>
);
}

export default Coin;
