          setModalToken = {setModalToken}
          setVisble = {setVisble}
        />
        <Coin
          perc={btc}
          setPerc={setBtc}
          token={"BTC"}
          setModalToken = {setModalToken}
          setVisble = {setVisble}
        />
        <Coin
          perc={btc}
          setPerc={setBtc}
          token={"ETH"}
          setModalToken = {setModalToken}
          setVisble = {setVisble}
